definition.script:
pipeline {
    agent any

    environment {
        DOCKERHUB = credentials('dockerhub')
        IMAGE = "varshika05/bluegreen-app:${BUILD_NUMBER}"
    }

    stages {
        stage('Clone Repo') {
            steps {
                git branch: 'main',
                    url: 'https://github.com/VarshikaAnand/bluegreen.git'
            }
        }

        stage('Build Docker Image') {
            steps {
                sh "docker build -t ${IMAGE} ."
            }
        }

        stage('Push Image') {
            steps {
                sh "echo ${DOCKERHUB_PSW} | docker login -u ${DOCKERHUB_USR} --password-stdin"
                sh "docker push ${IMAGE}"
            }
        }

        stage('Blue-Green Deploy') {
            steps {
                sh "bash scripts/deploy_blue_green.sh ${IMAGE}"
            }
        }
    }
}
